mapper.select:
select m.*,s.name standard from material_item m LEFT JOIN standard s on m.standard_id = s.id and m.yn = s.yn where m.yn=1
and m.main_id = #{param.mainId}
and m.status = #{param.status}
and m.brand like CONCAT('%', #{param.brand,jdbcType=VARCHAR}, '%')
and m.model_name like CONCAT('%', #{param.modelName,jdbcType=VARCHAR}, '%')
ORDER BY m.modified desc
